mapper.select:
select * 
from singer1 s


	s.first_name = #{firstName}


	and s.last_name = #{lastName}

select last_name || ' ' || first_name
from singer1 s
where	s.id = #{id}

select s.id, s.first_name, s.last_name, s.birth_date
from	singer1 s
where	s.id = #{id}

select first_name
from singer1 s
where	s.id = #{id}

mapper.update:
update singer1
set		first_name = #{firstName},
		last_name = #{lastName},
		birth_date = #{birthDate}
where	id = #{id}
